import React , { useState, useEffect } from 'react';
import { MDBDataTable } from 'mdbreact';
const axios = require('axios');

const DatatablePage = () => {

    const [rows, setRows] = useState();


   useEffect( () => {




    axios.get('https://hpb.health.gov.lk/api/get-current-statistical')
        .then(response => {
            //console.log(response.data.data);
            console.log(response.data.data.hospital_data);

            const hos = response.data.data.hospital_data;

            console.log('hos',hos);
            const rows = hos.map(hos => (

                { 

                id: hos.id, 
                HospitalName : hos.hospital.name,
                treated_local : hos.cumulative_local,
                treated_foreign : hos.cumulative_foreign,
                treating_local : hos.treatment_local,
                treating_foreign : hos.treatment_foreign




                }));
           console.log(rows);

           setRows(rows);
        });


      },[])
  const data = {
    columns: [

      {
        label: 'id',
        field: 'id',
        sort: 'asc',
        width: 10
      },
      {
        label: 'HospitalName',
        field: 'HospitalName',
        sort: 'asc',
        width: 10
      },
      {
        label: 'treated_local',
        field: 'treated_local',
        sort: 'asc',
        width: 15
      },
      {
        label: 'treated_foreign',
        field: 'treated_foreign',
        sort: 'asc',
        width: 10
      },
      {
        label: 'treating_local',
        field: 'treating_local',
        sort: 'asc',
        width: 10
      },
      {
        label: 'treating_foreign',
        field: 'treating_foreign',
        sort: 'asc',
        width: 10
      }

    ],
    rows: rows
  };

  console.log('rooooo',rows);

  return (
    <MDBDataTable
      striped
      bordered
      hover
      data={data}
    />
  );
}

export default DatatablePage;
