import React , { useState, useEffect } from 'react';
import { MDBDataTable } from 'mdbreact';
const axios = require('axios');

const DatatablePage = () => {

    const [rows, setRows] = useState();


   useEffect( () => {




    axios.get('https://hpb.health.gov.lk/api/get-current-statistical')
        .then(response => {
            //console.log(response.data.data);
            console.log(response.data.data.hospital_data);

            const hos = response.data.data.hospital_data;

            console.log('hos',hos);
            const rows = hos.map(hos => (

                { 

                id: hos.id, 
                treated_local : hos.cumulative_local,
                treated_foreign : hos.cumulative_foreign,
                hos_name : hos.hospital.name




                }));
           console.log(rows);

           setRows(rows);
        });


      },[])
  const data = {
    columns: [
      {
        label: 'Name',
        field: 'name',
        sort: 'asc',
        width: 150
      },
      {
        label: 'Position',
        field: 'position',
        sort: 'asc',
        width: 270
      },
      {
        label: 'Office',
        field: 'office',
        sort: 'asc',
        width: 200
      },
      {
        label: 'Age',
        field: 'age',
        sort: 'asc',
        width: 100
      },
      {
        label: 'Start date',
        field: 'date',
        sort: 'asc',
        width: 150
      },
      {
        label: 'Salary',
        field: 'salary',
        sort: 'asc',
        width: 100
      }
    ],
    rows: rows
  };

  console.log(rows);

  return (
    <MDBDataTable
      striped
      bordered
      hover
      data={data}
    />
  );
}

export default DatatablePage;
